import {useContext, useState} from "react"
import {AppContext} from "../Context";

function TextBoxes({translateText}) {

  const {sourceLang, setSourceLang, targetLang, setTargetLang} = useContext(AppContext)

  function switchLang(){

    setSourceLang(targetLang)
    setTargetLang(sourceLang)

  }

  return (

    <>
      <div className="text-box-container">
        <textarea className="text-box" placeholder={sourceLang == "en"? "Enter a word or phrase in English..." : "Saisir un mot ou une expression en français"}/>
          <button className="text-box-center" onClick={switchLang} >⮂</button>
        <textarea className="text-box" disabled/>
      </div>

      <div className="translate-button-container">
        <button className="text-box-buttons" onClick={translateText}>translate</button>
        <button className="text-box-buttons">save</button>
        <button className="text-box-buttons">clear</button>
      </div>
  </>

  );
}

export default TextBoxes;
