import { useContext, useState } from "react"
import { AppContext } from "../Context"

const CategoryModal = ({setShowModal}) => {
    const {user, setUser} = useContext(AppContext)

    const [wordCat, setWordCat] = useState(null)
    const [expCat, setExpCat] = useState(null)
    const [wordActive, setWordActive] = useState()
    const [expActive, setExpActive] = useState()



    function activate(){
    if(wordCat != null)
    {setExpActive("disabled"); setWordActive("")}
    else if (expCat != null)
    {setExpActive(""); setWordActive("disabled")}
    else
    {setExpActive(""); setWordActive("")}
}

    const expMap = user.expressions.map((exp) => <option disabled={expActive}>{exp.french}</option>)
    const wordMap = user.words.map((word) => <option disabled={wordActive}>{word.french}</option>)

    console.log("word", wordActive)
    console.log("exp", expActive)



    return(
<div className="overlay">
        <div className="add-category-container">
            <label className="modal-label">Category</label>
            <input className="modal-input"  />

            <p>Select one of the following</p>
            <div className="category-type-container" >
                 <label className="modal-label">Word</label>
                <select value={wordCat} className="modal-input" onChange={(e) => {setWordCat(e.target.value); activate()}}>
                    <option value={null} defaultValue >-Select Word-</option>
                    {wordMap}
                    </select>
                <label className="modal-label">Expression</label>
                <select value={expCat} className="modal-input" onChange={(e) => {setExpCat(e.target.value);activate()}}>
                <option value={null} defaultValue >-Select Expresion-</option>
                    {expMap}
                </select>
            </div>

            <div className="modal-button-container">
                <button id="modal-save"  >save</button>
                <button id="modal-cancel" onClick={() => setShowModal(false)}>cancel</button>
            </div>

        </div>

</div>

    )
}

export default CategoryModal
