import { useContext, useState } from "react"
import { AppContext } from "../Context"

const CategoryModal = ({setShowModal}) => {



    const {user, setUser} = useContext(AppContext)

    const expMap = user.expressions.map((exp) => <option>{exp.french}</option>)
    const wordMap = user.words.map((word) => <option>{word.french}</option>)

    return(
<div className="overlay">
        <div className="add-category-container">
            <label className="modal-label">Category</label>
            <input className="modal-input"  />
            <label className="modal-label">Word</label>
            <select className="modal-input">
                <option default>-Select Word-</option>
                {wordMap}
                </select>
            <label className="modal-label">Expression</label>
            <select className="modal-input">
                {expMap}
            </select>

            <div className="modal-button-container">
                <button id="modal-save"  >save</button>
                <button id="modal-cancel" onClick={() => setShowModal(false)}>cancel</button>
            </div>

        </div>

</div>

    )
}

export default CategoryModal
